import React, {useState} from 'react';
import './App.scss';

function App() {
  /*
    todos - name of state
    setTodos - used to set the state
    useState - hook into state or lifecycle of App component
  */
  const [todos, setTodos] = useState([
    {task: 'Help Mom with the groceries'},
    {task: 'Attend night school'},
    {task: 'Play video games'},
    {task: 'Read comic books'},
    {task: 'Hang out with friends'},
    {task: 'Go out for a walk'},
  ]);

  // Todo Component
  const Todo = ({todo}) => <div className="todo">{todo.task}</div>

  // Form Input; Add things to state
  const [value, setValue] = useState('');

  // Handle Submit
  const handleSubmit = event => {
    event.preventDefault();
    // If there is no value entered, it cannot be submitted
    if(!value) {
      return; 
      addTodo(value);
      setValue('');
    }
  };

  return (
    <div className="app">
      <p>Todo List</p>
      <div className="app__todolist">
        {todos.map((todo, index) => (
          <Todo key={index} index={index} todo={todo} />
        ))}
      </div>
    </div>
  );
}

export default App;
