import React, {useState} from 'react';
import FormInput from './Components/FormInput/FormInput';
import Todo from './Components/Todo/Todo'
import './App.scss';

function App() {  
  /*
    todos - name of state
    setTodos - used to set the state
    useState - hook into state or lifecycle of App component
  */
  const [todos, setTodos] = useState([
    {display: 'Help Mom with the groceries', isDone: false},
    {display: 'Attend night school', isDone: false},
    {display: 'Play video games', isDone: false},
    {display: 'Read comic books', isDone: false},
    {display: 'Hang out with friends', isDone: false},
    {display: 'Go out for a walk', isDone: false},
  ]);

  // Add todo item
  const addTodo = display => {
    const newTodos = [...todos, {display}];
    setTodos(newTodos); // setTodos - sets the state
  };

  // Complete a todo item
  const finishTodo = index => {
    const newTodos = [...todos];
    newTodos[index].isDone = true;
    setTodos(newTodos);
  };

  // Delete a todo item
  const deleteTodo = index => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos); 
  }

  return (
    <div className="app">
      <p>Todo List</p>
      <div className="app__todolist">
        {todos.map((todo, index) => (
          <Todo display={index} index={index} todo={todo} finishTodo={finishTodo}/>
        ))}
        <FormInput addTodo={addTodo}/>
      </div>
    </div>
  );
}

export default App;
