import React, {useState} from 'react';
import './App.scss';

const Todo = ({todo}) => <div className="todo">{todo.key}</div>
// Todo Component
function FormInput({addTodo}) {
  // Form Input; Add things to state
  const [value, setValue] = useState('');

  // Handle Submit
  const handleSubmit = event => {
    event.preventDefault();
    // If there is no value entered, it cannot be submitted
    if(!value) {
      return; 
      addTodo(value);
      setValue('');
    }
  };

  return (      
    <form onSubmit={handleSubmit}>
      <input 
        type="key" 
        className="input" 
        value={value} 
        onChange={event => setValue(event.target.value)}
      />
    </form>
  );
}

function App() {
  /*
    todos - name of state
    setTodos - used to set the state
    useState - hook into state or lifecycle of App component
  */
  const [todos, setTodos] = useState([
    {key: 'Help Mom with the groceries'},
    {key: 'Attend night school'},
    {key: 'Play video games'},
    {key: 'Read comic books'},
    {key: 'Hang out with friends'},
    {key: 'Go out for a walk'},
  ]);

  // Add todo item
  const addTodo = key => {
    const newTodos = [...todos, {key}];
    setTodos(newTodos); // setTodos - sets the state
  };

  return (
    <div className="app">
      <p>Todo List</p>
      <div className="app__todolist">
        {todos.map((todo, index) => (
          <Todo key={index} index={index} todo={todo} />
        ))}
        <FormInput addTodo={addTodo} className="add__todolist" />
      </div>
    </div>
  );
}

export default App;
