import React, {useState} from 'react';
import './App.scss';

function App() {
  /*
    todos - name of state
    setTodos - used to set the state
    useState - hook into state or lifecycle of App component
  */
  const [todos, setTodos] = useState([
    {text: 'Help Mom with the groceries'},
    {text: 'Attend night school'},
    {text: 'Play video games'},
    {text: 'Read comic books'},
    {text: 'Hang out with friends'},
    {text: 'Go out for a walk'},
  ]);

  // Todo Component
  const Todo = ({todo}) => <div className="todo">{todo.text}</div>

  // Form Input; Add things to state
  const [value, setValue] = useState('');

  // Handle Submit
  const handleSubmit = event => {
    event.preventDefault();
    // If there is no value entered, it cannot be submitted
    if(!value) {
      return; 
    }
    addTodo(value);
    setValue('');
  };

  // Add todo item
  const addTodo = todo => {
