  return (
    <div className="app">
      <p>Todo List</p>
      <div className="app__todolist">
        {todos.map((todo, index) => (
          <Todo key={index} index={index} todo={todo} />
        ))}
      </div>
      <form onSubmit={handleSubmit}>
          <input 
            type="text" 
            className="input" 
            value={value} 
            onChange={event => setValue(event.target.value)}/>
      </form>
    </div>
  );
}

export default App;
